export const catcher =
  <T extends (...args: any[]) => Promise<any>>(
    f: T
  ): ((...parameters: Parameters<T>) => void) =>
  (...parameters: Parameters<T>) =>
    f(...parameters).catch((e) => {
      console.error(e);
    });
